import React, {useState} from 'react';
import {StyleSheet, Text, View, TextInput, TouchableOpacity, Alert} from 'react-native';
const server_ip = 'https://14fb-77-137-180-250.ngrok.io'


export default function Login({navigation}){
    const [email='', setEmail] = useState()
    const [password='', setPassword] = useState()

    const loginUser = async() => {
        let response = await fetch('https://14fb-77-137-180-250.ngrok.io/login', {
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              user_email: email,
              user_password: password
            })
        })
        let json = await response.json()
        if (json === "Login successful")
            Alert.alert('',json,[{text: "Ok", onPress: navigation.reset({
                index:0,
                routes:[
                  {
                    name:"LoginSuccess",
                  },
                ]})}])
        else 
            Alert.alert('',json)
    }

    return (
        <View style={styles.regform}>
            <Text style={styles.header}>Login to your account</Text>
            <TextInput style={styles.textinput} placeholder="Email" underlineColorAndroid={"transparent"} onChangeText={(val) => setEmail(val)}/>
            <TextInput style={styles.textinput} placeholder="Password" underlineColorAndroid={"transparent"} secureTextEntry={true} onChangeText={(val) => setPassword(val)}/>
            <TouchableOpacity style={styles.button} onPress={() => loginUser()}>
                <Text style={styles.buttontext}>Login</Text>
            </TouchableOpacity>
        </View>
    );
}

const styles = StyleSheet.create({
    regform:{
        alignSelf: "stretch",
        flex: 1,
        paddingLeft: 60,
        paddingRight: 60,
        justifyContent: "center"
    },
    header:{
        fontSize: 24,
        color: "black",
        paddingBottom: 10,
        marginBottom: 40,
        borderBottomColor: "#199187",
        borderBottomWidth: 1,
    },
    textinput:{
        alignSelf: "stretch",
        height: 40,
        marginBottom: 30,
        color: "grey",
        borderBottomColor: "#199187",
        borderBottomWidth: 1,
    },
    button: {
        alignSelf: "stretch",
        alignItems: "center",
        padding: 20,
        backgroundColor: "grey",
        marginTop: 30,
    },
    buttontext:{
        color: "#fff",
        fontWeight: "bold"
    }
})
